})
export class NavigationComponent {

  isLogged: boolean = false;

  constructor() {

  }
}
<header class="header">
    <a href="#" class="logo">CarNaMall</a>
    <nav class="navbar">
        <a href="#">Home</a>
        <a href="#">Cars</a>
        <a href="#">Services</a>
        <a href="#">About</a>
