import { Component, OnInit } from '@angular/core';
import { ApiService } from 'src/app/services/api.service';

export interface Car {
  model: string;
  doors: number;
  seats: number;
  transmission: string;
  price: number;
  image: string;
  year: number;
  ownerId: string;
}
@Component({
  selector: 'app-carslist',
  templateUrl: './carslist.component.html',
  styleUrls: ['./carslist.component.css']
})


export class CarslistComponent implements OnInit{
  carsLister: Car[] | undefined;
  isLoaded = false;

  constructor(private api: ApiService){}

  ngOnInit(): void {
    setTimeout(()=> {
      this.getRentss();
      this.isLoaded = true;
    }, 1000);
    setTimeout(()=> {
      console.log(this.carsLister)
    }, 1100)

  }

getRentss() { 
    this.api.getRents().subscribe((res) => {
      this.carsLister = res;
    });
  }

}
<div class="containercarslist">
    <h2>Our Car Park</h2>
    <p>Here you can find our list of all available cars. You can pick the one like.</p>
  </div>
  <div *ngIf="!isLoaded">
    Loading...
    </div>
  <div class="carsdiv" *ngIf="isLoaded">
  <div class="carscontainer">
    <div class="item-1 colrow" *ngFor="let car of carsLister">
        <a href="#"><img src={{car.image}} alt="Image" class="img-fluid"></a>
        <div class="item-1-contents">
          <div class="text-center">
          <h3><a href="#">{{car.model}}</a></h3>
          <div class="rating">
            <span><i class="fa-solid fa-star"></i></span>
            <span><i class="fa-solid fa-star"></i></span>
            <span><i class="fa-solid fa-star"></i></span>
            <span><i class="fa-solid fa-star"></i></span>
            <span><i class="fa-solid fa-star"></i></span>
          </div>
          <div class="rent-price"><span>${{car.price}}/</span>day</div>
          </div>
          <ul class="specs">
            <li>
              <span>Doors</span>
              <span class="spec">{{car.doors}}</span>
            </li>
            <li>
              <span>Seats</span>
              <span class="spec">{{car.seats}}</span>
            </li>
            <li>
              <span>Transmission</span>
              <span class="spec">{{car.transmission}}</span>
            </li>
            <li>
              <span>Year</span>
              <span class="spec">{{car.year}}</span>
            </li>
          </ul>
          <div class="rent-action">
            <a href="contact.html" class="btn btn-primary">Rent Now</a>
          </div>
        </div>
      </div>
    </div>
  </div>
