import { AbstractControl } from "@angular/forms";


export function passwordMatch(password: string, rePassword: string) {
  
  return function(form: AbstractControl) {
    const passValue = form.get(password)?.value;
    const rePassValue = form.get(rePassword)?.value;


    if (passValue === rePassValue) {
        return null;
    }
    return {passwordMissmatchError: true}
  }

}
