import { Component } from '@angular/core';
import { AuthService } from 'src/app/services/auth.service';
import { AuthenticateComponent } from '../authenticate/authenticate.component';

@Component({
  selector: 'app-navigation',
  templateUrl: './navigation.component.html',
  styleUrls: ['./navigation.component.css']
})
export class NavigationComponent {


  constructor(private auth: AuthService, private authcom: AuthenticateComponent) {}

  isLogged: boolean = this.authcom.isAuthenticating;

  logOut() {
    this.auth.logOut();
  }
}
<header class="header">
    <a href="#" class="logo">CarNaMall</a>
    <nav class="navbar">
        <a routerLink="/home">Home</a>
        <a routerLink="/cars">Cars</a>
        <a routerLink="/services">Services</a>
        <a routerLink="/about">About</a>
        <a routerLink="/contact">Contact</a>
        <div class="userA" *ngIf="!isLogged">
            <a routerLink="/register">Register</a>
            <a routerLink="/login">Login</a>
        </div>
        <div class="userB" *ngIf="isLogged">
            <a routerLink="/create">Create</a>
            <a routerLink="/profile">Profile</a>
            <a (click)="logOut()">Logout</a>
        </div>
    </nav>
</header>
