import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { NgToastService } from 'ng-angular-popup';
import { firstValueFrom } from 'rxjs';
import { ApiService } from 'src/app/services/api.service';
import { AuthService } from 'src/app/services/auth.service';
import ValidateForm from 'src/app/validators/validateforms';

@Component({
  selector: 'app-create',
  templateUrl: './create.component.html',
  styleUrls: ['./create.component.css']
})
export class CreateComponent implements OnInit {

  createForm!: FormGroup;
  usera: any = {};

  constructor(private fb: FormBuilder, private toast: NgToastService, private api: ApiService, private auth: AuthService,
    private router: Router) {}

 ngOnInit(): void {
  this.createForm = this.fb.group({
    model: ['', Validators.required],
    image: ['', Validators.required],
    doors: ['', Validators.required],
    seats: ['', Validators.required],
    transmission: ['', Validators.required],
    price: ['', Validators.required],
    year: ['', Validators.required],

});

this.auth.user$.subscribe(
  value => {
    this.usera = value;
  }
);
console.log(this.usera)

 }



 onCreateRent(){
  if(this.createForm.valid){
    let createObj = this.createForm.value;
    createObj = {...createObj, ownerId: this.usera._id}
    this.api.createRent(createObj).subscribe({
      next: (res => {
        this.toast.success({detail: 'SUCCESS', summary: res.message, duration: 5000})
        this.createForm.reset();
        this.router.navigate(['cars']);
      }),
      error: (err) => {
        this.toast.error({detail: 'ERROR', summary: err.message, duration: 5000})

      }
    });
  } else {
    ValidateForm.validateAllFormFields(this.createForm);
    alert('Your form is invalid');
  }
 }


}
<div class="containercreate">
    <h2>Create a Card</h2>
    <p>Here you can create a post about the car which you are willing to lease to people with our website's help!</p>
  </div>

  <div class="backgroundcreate">
	<div class="containercr">
		<div class="contact-box">
			<div class="left"></div>
			<form class="right" [formGroup]="createForm">
				<h2>Create a Post</h2>
				<input formControlName="model" type="text" class="field" placeholder="Model">
				<input formControlName="image" type="text" class="field" placeholder="imageUrl">
				<input formControlName="doors" type="text" class="field" placeholder="Doors">
				<input formControlName="seats" type="text" class="field" placeholder="Seats">
				<input formControlName="transmission" type="text" class="field" placeholder="Transmission">
				<input formControlName="price" type="text" class="field" placeholder="Price/Day">
				<input formControlName="year" type="text" class="field" placeholder="Year">
				<button class="btn" (click)="onCreateRent()">Post</button>
			</form>
		</div>
	</div>
</div>
